import React, { useState } from 'react';

function LoginBox({ onSubmit }) {
  const [firstName, setFirstName] = useState('');
  const [email, setEmail] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    onSubmit(firstName, email);
  };

  // Enable submit only if both firstName and email are filled. No error mesages for now.
  const isFormFilled = firstName.trim() && email.trim();

  return (
    <form onSubmit={handleSubmit}>
      <label>
        First Name:
        <input
          type="text"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
        />
      </label>
      <label>
        Email:
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
      </label>
      <button type="submit" disabled={!isFormFilled}>Submit</button>
    </form>
  );
}

export default LoginBox;
