import React, { useState } from 'react';

function LoginBox({ onSubmit }) {
  const [firstName, setFirstName] = useState('');
  const [email, setEmail] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    onSubmit(firstName, email);
  };

  const isFormFilled = firstName.trim() && email.trim();

  const inputStyle = {
    margin: '10px 0', // Adds space around input fields
  };

  const buttonStyle = {
    padding: '10px 20px', // Makes the button larger
    backgroundColor: isFormFilled ? 'red' : 'grey', // Changes color when form is filled
    color: 'white',
    border: 'none',
    cursor: isFormFilled ? 'pointer' : 'not-allowed',
  };

  return (
    <form onSubmit={handleSubmit}>
      <label style={{ display: 'block', marginBottom: '5px' }}>
        First Name:
        <input
          type="text"
          value={firstName}
          onChange={(e) => setFirstName(e.target.value)}
          style={inputStyle}
        />
      </label>
      <label style={{ display: 'block', marginBottom: '5px' }}>
        Email:
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={inputStyle}
        />
      </label>
      <button type="submit" style={buttonStyle} disabled={!isFormFilled}>Submit</button>
    </form>
  );
}

export default LoginBox;
