import { useFlags, withLDProvider } from 'launchdarkly-react-client-sdk';
import HelloWorld from './helloWorld'; //We will add this code in the next step
import logo from './logo.svg'
import ldlogo from './launchdarkly-icon-logo.png'
import './App.css';
import Header from './components/header';
import LoginBox from './components/LoginBox';


function App() {
  const { animate, imageSwitch, bottomText, newHeader } = useFlags()
  const [firstName, setFirstName] = useState('Example user2');
  const [email, setEmail] = useState('User2@example2.com');

  const handleLoginSubmit = (inputFirstName, inputEmail) => {
    setFirstName(inputFirstName);
    setEmail(inputEmail);
  };
  return (
    <div className="App">
      <header className="App-header">
        {newHeader ? (<Header />): (<div />)}
        <LoginBox onSubmit={handleLoginSubmit} />
        { imageSwitch ? (<img src={ldlogo} className={animate} alt="logo" />) :( <img src={logo} className={animate} alt="logo" />)

        }
        <p>
          {bottomText}/n
          hello my baby hello my darlin'
        </p>

        <HelloWorld />

      </header>
    </div>
  );
}
// Update the export default to use your environment-specific client ID and a sample context:
export default withLDProvider({
  clientSideID: '656eb3fa552c3c10556c5ecf',
  context: {
    "kind": "user",
    "key": "example_user2",
    "name": "firstName",
    "email": "email"
  }
})(App);
