import React, { useState } from "react";
import { useLDClient } from "launchdarkly-react-client-sdk";

export default function LoginBox({ onSubmit }) {
  const LDClient = useLDClient();

  const [userState, setUserState] = useState({
    firstName: "",
    email: "",
  });

  const submitUser = async (e) => {
    e.preventDefault();
    if (LDClient) {
      LDClient.identify({
        key: userState.email,
        firstName: userState.firstName,
        email: userState.email,
      });
      LDClient.track('userLogin', { customProperty: userState.email });
      console.log("Logged in as: ", userState.firstName);
      setUserState({ firstName: "", email: "" }); // Clear the form fields
    }
  };

  const handleChange = (e) => {
    const { id, value } = e.target;
    setUserState((prevState) => ({
      ...prevState,
      [id]: value,
    }));
  };

  return (
    <div className="login-container">
      <form onSubmit={submitUser}> {/* Make sure this matches your existing form's submit function */}
        <input
          className="login-input"
          type="text"
          id="firstName"
          placeholder="Enter First Name"
          value={userState.firstName}
          onChange={handleChange}
          // ... (any other props you have)
        />
        <input
          className="login-input"
          type="email"
          id="email"
          placeholder="Enter Email"
          value={userState.email}
          onChange={handleChange}
          // ... (any other props you have)
        />
        <button className="login-button" type="submit">Submit</button>
      </form>
    </div>
  );
}
