import React, { useEffect } from 'react';
import { useFlags, withLDProvider, useLDClient } from 'launchdarkly-react-client-sdk';
import logo from './logo.svg';
import ldlogo from './launchdarkly-icon-logo.png';
import './App.css';
import AdBanner from './components/AdBanner'
import AdBanner2 from './components/AdBanner2'
import Header from './components/header';
import LoginBox from './components/LoginBox';

function App() {
  const { advertisementBanner, animate, imageSwitch, newHeader } = useFlags();
  const ldClient = useLDClient();

  const handleLoginSubmit = (firstName, email) => {
    if (ldClient) {
      ldClient.identify({
        key: email,
        name: firstName,
        email: email
      });
    }
  };

  return (
    <div className="App">
      <h1 className="App-header">
        {newHeader ? <Header /> : <div />}
        <LoginBox onSubmit={handleLoginSubmit} />
        {imageSwitch ? <img src={ldlogo} className={animate} alt="logo" /> : <img src={logo} className={animate} alt="logo" />}
        <p>
          <br />
        </p>
      </h1>
      {advertisementBanner ? <AdBanner /> : <AdBanner2 />}
    </div>
  );
}

export default withLDProvider({
  clientSideID: '656eb3fa552c3c10556c5ecf',
  context: {
    kind: "user",
    key: "anonymous"
  }
})(App);
